<script>
  //import { send } from "vite";
  import { onSend, startConnect } from "../stores/mqttStore";
  import { isConnected } from "../stores/store";

  // Function to send MQTT message
  // @ts-ignore
  function sendMessage(direction) {
    const message = direction;
    onSend("Driverbot", message);
  }
  // Read WASD keys for movement
  function buttonPress() {
    document.addEventListener("keydown", function (event) {
      if (event.key === "w") {
        sendMessage("forward");
      } else if (event.key === "a") {
        sendMessage("left");
      } else if (event.key === "s") {
        sendMessage("backwards");
      } else if (event.key === "d") {
        sendMessage("right");
      } else if (event.key === "e") {
        sendMessage("stop");
      } else if (event.key === "r") {
        sendMessage("record");
      } else if (event.key === "p") {
        sendMessage("play");
      }
    });
  }
</script>

<div class="flex justify-center bg-slate-600 -500 space-x-4">
  <div class="flex flex-col items-center space-y-2 bg-white p-4 rounded shadow">
    {#if $isConnected}
      <img
        src="data:image/jpeg;base64,"
      />
      <p class="text-green-500">Connected</p>
    {:else}
      <p class="text-red-500">Not Connected</p>
    {/if}
    <!-- Top Button -->
    <div>
      <button
        class="btn variant-filled"
        on:click={() => sendMessage("forward")}
      >
        Forward (w)</button
      >
      <button class="btn variant-filled" on:click={() => sendMessage("stop")}
        >Stop (e)</button
      >
      <button class="btn variant-filled" on:click={() => sendMessage("record")}
        >Record (r)</button
      >
    </div>

    <!-- Middle Row of Buttons-->
    <div class="flex justify-between space-x-2 w-full">
      <button class="btn variant-filled" on:click={() => sendMessage("left")}>
        Left (a)
      </button>
      <button
        class="btn variant-outline-secondary bg-cyan-300"
        on:click={buttonPress}
        disabled={$isConnected}
        on:click={startConnect}
      >
        Connect
      </button>
      <button class="btn variant-filled" on:click={() => sendMessage("right")}>
        Right (d)
      </button>
    </div>

    <!-- Bottom Button -->
    <div>
      <button
        class="btn variant-filled"
        on:click={() => sendMessage("backwards")}
      >
        Backwards (s)
      </button>
      <button class="btn variant-filled" on:click={() => sendMessage("play")}
        >Play recording (p)</button
      >
    </div>
  </div>
</div>

